import React from "react";
import { useParams } from "react-router-dom";

const House = () => {
  const params = useParams();

  const [houseCharacters, setHouseCharacters] = useState([]);

  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchHouseCharacters = async (url, signal) => {
    setIsLoading(true);
    setError(null);

    try {
      const res = await fetch(url, { signal });

      if (res.status !== 200) {
        throw new Error("Something went wrong.");
      }

      const data = await res.json();

      setHouseCharacters(data);
    } catch (err) {
      setError(err.message);
    }

    setIsLoading(false);
  };

  useEffect(() => {
    const url = "http://hp-api.herokuapp.com/api/characters/" + params.house;

    const controller = new AbortController();
    fetchHouseCharacters(url, controller.signal);

    return () => {
      controller.abort();
    };
  }, []);

  let hogwartsHouseCharacters = houseCharacters.map((character, index) => {
    return (
      <div key={index}>
        <img src={character.image} alt={character.name} />
        <p>{character.name}</p>
      </div>
    );
  });
  return (
    <section>
      <h1>{params.house}</h1>
    </section>
  );
};

export default House;
