import React, { useState } from "react";
import StudentModal from "./StudentModal";

const Student = (props) => {
  const [display, setDisplay] = useState(false);
  const [studentData, setStudentData] = useState({
    name: "",
    alternate_names: [],
    species: "",
    gender: "",
    house: "",
    dateOfBirth: "",
    yearOfBirth: 0,
    wizard: false,
    ancestry: "",
    eyeColour: "",
    hairColour: "",
    wand: {},
    patronus: "",
    hogwartsStudent: false,
    hogwartsStaff: false,
    actor: "",
    alternate_actors: [],
    alive: false,
    image: "",
  });

  const handleStudentClick = (event) => {
    event.preventDefault();
    for (const student of props.students) {
      if (event.target.src == student.image) {
        setStudentData({
          name: student.name,
          alternate_names: student.alternate_names,
          species: student.species,
          gender: student.gender,
          house: student.house,
          dateOfBirth: student.dateOfBirth,
          yearOfBirth: student.yearOfBirth,
          wizard: student.wizard,
          ancestry: student.ancestry,
          eyeColour: student.eyeColour,
          hairColour: student.hairColour,
          wand: student.wand,
          patronus: student.patronus,
          hogwartsStudent: student.hogwartsStudent,
          hogwartsStaff: student.hogwartsStaff,
          actor: student.actor,
          alternate_actors: student.alternate_actors,
          alive: student.alive,
          image: student.image,
        });
      }
    }

    setDisplay(true);
  };

  const handleModalReturn = () => {
    setDisplay(false);
  };

  let students = props.students.map((student, index) => {
    return (
      <div>
        <img
          src={student.image}
          alt={student.name}
          key={index}
          onClick={handleStudentClick}
        />
        <p>{student.name}</p>
        <p>{student.house}</p>
      </div>
    );
  });
  return (
    <>
      {display && (
        <StudentModal
          studentData={studentData}
          returnClicked={handleModalReturn}
        ></StudentModal>
      )}
      {students}
    </>
  );
};

export default Student;
