import React from "react";
import ReactDOM from "react-dom";

import styles from "./StudentModal.module.css";

const OverLay = (props) => {
  return (
    <div className={styles.backdrop} onClick={props.doneClicked}>
      <div className={`${styles.board} ${styles.modal}`}>
        <header className={styles.header}>
          <h2>{props.students.name}</h2>
        </header>
        <div className={styles.content}>
          <p>
            {props.students.gender} {props.students.ancestry}{" "}
            {props.students.species}
            {props.students.wizard ? " wizard" : ""}
          </p>
          <p>House: {props.students.house}</p>
          <p>DOB: {props.students.dateOfBirth}</p>
          <p>Eye colour: {props.students.eyeColour}</p>
          <p>
            Wand: {props.students.wand.wood} wood; {props.students.wand.core}{" "}
            core; {props.students.wand.length}"
          </p>
          <p>Patronus: {props.students.patronus}</p>
        </div>
        <footer className={styles.actions}>
          <button onclick={props.doneClicked}>Done</button>
        </footer>
      </div>
    </div>
  );
};

const StudentModal = (props) => {
  return <div></div>;
};

export default StudentModal;
